// show/hide hourglass
function twirlyThing(enable) {
    app.twirlyThing(app, enable);
}

function showMessage(title, mess) {
    var value = '<span style="font-weight: bold;">' + title + "</span>" + (mess ? (': ' + mess) : '');   
    app.w("message").value(value);

    var timeoutId = setTimeout(function() {
        app.w("message").value('');
        clearTimeout(timeoutId);
    }, 3000); 
}

function trim(str) {
   return (str) ? str.replace(/^\s+|\s+$/g, '') : '';
}

function assert(assertion, message){
    if (!assertion) {
        throw message ? message : "An exception was occured";
    }
}

function isValidEmail(value) {
    reg = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
    if (!value.match(reg)) {
        return false;
    } 
    return true;
}

