function updateUserData(user) {
    LocalDBRequester.addOrUpdateUser({
            id_user: user._id,
            email: user.email,
            name : user.name,
            phone: user.phone,
            country: user.country,
            state: user.state,
            zipcode : user.zipcode,
            primary_license : user.primary_license,
            secondary_license : user.secondary_license,
        }).done(function() {
            g_curUser.user(user);            
            applyUseridToLocalCerts();

            app.pageJump(MAIN_PAGE);
        }).fail(function(err) {
            showMessage('Login FAILED at the Server', err);    
        });
}

