/**************************/
/*          SYNC          */
/**************************/
function onSync() {
    if (g_curUser.isDefault()) {
        showMessage("Error", "Please sign in before");
        return;
    }

    twirlyThing(true);
    sync(g_curUser.getID()).done(function() {
        showMessage('Sync OK');
    }).fail(function(err) {
        showMessage('Sync FAILED', JSON.stringify(err));
	}).always(function () {
        twirlyThing(false)        
    });
}

/**************************/
/*      REFRESH LIST      */
/**************************/
function refreshCertsList() {
    var wCertsList = app.w('certsList');
    // uses local db to retrieve certs data
    LocalDBRequester.getUserCerts(g_curUser.getID(), null, true).done(function(certs) {

        var headerLabel = '<div class="cell img">Img</div>'
                + '<div class="cell name">Cert Name</div>'
                + '<div class="cell number">#ID</div>'
                + '<div class="cell date">Exp Date</div>';

        var data = [{
            label : headerLabel,
            value : 0,
            divider: true
        }];
        $.each(certs, function(index, cert) {
            var images = chooseImagesForCert(cert);
            var date = new Date();
            date.setTime(cert.expired_date);
            var expiredDate =  (date.getUTCMonth() + 1) + "/" + date.getUTCDate() + "/" + date.getUTCFullYear();
            //var imgHtml = (images.thumb) ? '<img class="m-ui-li-icon m-ui-li-thumb" src="' + images.thumb + '" style="max-width: 25px;"/>' : '';
            var imgHtml = (images.thumb) ? '<div style="height: 22px; width: 22px; overflow: hidden;"><img src="' + images.thumb + '" style="min-width: 22px; height: 22px;"/></div>' : '';

            var label = '<div class="cell img">' + imgHtml + ' </div>'
                + '<div class="cell name">' + cert.name + ' </div>'
                + '<div class="cell number">#' + cert.cert_number + ' </div>'
                + '<div class="cell date">' + expiredDate + ' </div>';

            data.push({
                label: label,
                value: cert.id_cert,
                //image: images.thumb,
                cert : cert,
                //aside: images.medium
            });
        });

        wCertsList.data(data);        
    }).fail(function () {
        showMessage('Refresh FAILED');
        wCertsList.data([]);
    });
}


/**************************/
/*       ADD CERT         */
/**************************/
function onAddCert() {
    var imageUri = app.w('img_CertImage').value();
    // certificate data structure
    var cert = {
        id_user: g_curUser.getID(), 
        id_cert: generateId(),
        expired_date: _getUTCTimestamp(app.w('date_certExpDate').value()),
        last_modified: _getUTCTimestamp(new Date()),
        name: g_certNameController.getValue(),
        cert_number: app.w('txt_certNumber').value(),
        local_medium: imageUri,
        local_thumb: imageUri
    };    

    // call local db method to save the cert
    LocalDBRequester.addCert(cert).done(function() {
        g_certNameController.setValue('');
        app.w('txt_certNumber').value('');
        app.w('img_CertImage').value('');
        showMessage('Add OK'); 
        app.pageBack();
    }).fail(function() {
        showMessage('Add FAILED');   
    });
}

function openImage(imageSrc) {
    if (imageSrc) {
        window.open(imageSrc, '_blank');
    }    
}

function chooseImagesForCert(cert) {
    var res = {
        thumb: undefined,
        medium: undefined
    };

    if (_isPhonegapEnabled()) {
        res.thumb = (cert.local_thumb) ? cert.local_thumb : cert.local_medium;
        res.medium = cert.local_medium;
    } else {
        res.thumb = cert.remote_thumb;
        res.medium = cert.remote_medium;
    }

    return res;
}

function getLinkToImage(cert) {
    return (cert && cert.remote_medium) ? cert.remote_medium : null;
}

