/*
    Internal function to generate uniqueIds.
    Used for Certificate Id generation. (User Id generates on the server side)
    @return String
*/
function generateId() {
    return 'i' + (new Date()).getTime();
}

/*
    Internal function to get UTC timestamp from javascript date object
    @return time in milliseconds
*/
function _getUTCTimestamp(dateObj) {
    return dateObj.getTime();
}

/*
    Internal utility function to checi is SQLite database field value is empty
 */
function _isSQLiteNull(val) {
    var res = false;
    if (val == null || val == undefined) {
        res = true;
    } else {
        val = val.toLowerCase();
        if (val == 'null' || val == 'undefined' || val.length == 0) {
            res = true;
        }
    }

    return res;
}

/*
    Intermal function to check PhoneGap library methods
    @return bool
*/
function _isPhonegapEnabled() {
    var cordova = null,
        res = true;
    if (window["PhoneGap"] != undefined){
        cordova = window["PhoneGap"];
    }
    else if (window["cordova"] != undefined){
        cordova = window["cordova"];
    }
    else if (window["Cordova"] != undefined){
        cordova = window["Cordova"];
    }

    if (!cordova) {
        console.log('PhoneGap DISABLED: global image urls mode');
        res = false;
    }

    return res;
}

/*
    This function is used to sync data between local database and the server.
    In case of success all local certs will be uploaded to the server.
    Then will be downloaded all updates from the server (based on the last sync date).
    After it last sync date will be updated.
    @param userId Id of the CertMaster user
    @return jQuery Deferred object
*/
function sync(userId) {
    var dfd = $.Deferred();

    var syncFail = function(err) {
        dfd.reject(err);
    };

    var newSyncDate = _getUTCTimestamp(new Date());

    LocalDBRequester.getLastSyncDate(userId).done(function(lastSyncDate) {
        upload(userId, lastSyncDate).done(function() {
            download(userId, lastSyncDate).done(function(certs) {
                LocalDBRequester.updateLastSyncDate(userId, newSyncDate).done(function() {
                    dfd.resolve();
                }).fail(syncFail);
            }).fail(syncFail);
        }).fail(syncFail);                                
    }).fail(syncFail);    

    return dfd;
}

/*
    This function downloads all up to date certs list from the server and add them to the local database.
    @param userId Id of the CertMaster user
    @param lastSyncDate Time in milliseconds.    
    @return jQuery Deferred object
*/
function download(userId, lastSyncDate) {
    var dfd = $.Deferred();
    var syncFail = function(err) {        
            dfd.reject(err);
        },
        downlaodCertImages = function(imageObj) {
            var dfd = $.Deferred();

            $.when(CertMasterFileProcesser.download(imageObj.medium, true), CertMasterFileProcesser.download(imageObj.thumb, true)).done(function(mediumUri, thumbUri) {
                dfd.resolve({
                    local_medium: mediumUri,
                    local_thumb: thumbUri
                });                    
            }).fail(function(err) {
                dfd.reject(err);
            });            
            return dfd;
        }

    ApiRequester.getUserCerts(userId, lastSyncDate).done(function(newCerts) {
        var total = newCerts.length,
            counter = 0,
            processCert = function(item) {
                item.id_user = userId;
                if (item.image) {
                    item.remote_medium = item.image.medium;
                    item.remote_thumb = item.image.thumb;
                }

                delete item.image;

                LocalDBRequester.addOrUpdateCert(item).done(function() {
                    counter++;
                    if (counter == total) {
                        dfd.resolve();
                    }
                }).fail(syncFail);
            };

        (total == 0) && dfd.resolve();

        $.each(newCerts, function(index, item) {            
            if (_isPhonegapEnabled() && item.image) {
                downlaodCertImages(item.image).done(function(localUriObj) {
                    $.extend(item, localUriObj);
                    processCert(item);
                }).fail(syncFail);                
            } else {
                processCert(item);
            }                
        });                        
    }).fail(syncFail);

    return dfd;
}

/*
    This function upload new certs from the local database to the server.
    @param userId Id of the CertMaster user
    @param lastSyncDate Time in miliseconds.
    @return jQuery Deferred object
*/
function upload(userId, lastSyncDate) {
    var dfd = $.Deferred();
    var syncFail = function(err) {        
        dfd.reject(err);
    };

    LocalDBRequester.getUserCerts(userId, lastSyncDate).done(function(certs) {
        var counter = 0,
            total = certs.length;

        (total == 0) && dfd.resolve();                    

        $.each(certs, function(index, item) {

            var cert = {
                id_cert: item.id_cert,
                last_modified: item.last_modified,
                expired_date: item.expired_date,
                name: item.name,
                cert_number: item.cert_number,
                image: item.local_medium
            };



            ApiRequester.uploadCert(userId, cert).done(function() {
                counter++;    
                if (counter == total) {
                    dfd.resolve();
                }
            }).fail(syncFail);
        });        
    }).fail(syncFail);

    return dfd;
}

files.SQLiteDatabaseAgent:
/*
    This is utility class that implements the basic DB methods such select, update, remove etc.
    This class doesn't include the business logic.
    Direct call of this methods in application code is not recommended.
    To get the full description of params see the link.
    @link http://www.applicationcraft.com/developers/documentation/ac-mobile-build-phonegap/ac-mobile-build/ac-build-plugins/sqlite-plugin/methods/
    @link http://api.jquery.com/category/deferred-object/
*/
var SQLiteDatabaseAgent = (function(){            
    var _db = null;

    var SQLiteDatabaseAgent = {
        /*
            This method have to be called first to init the class.
            This is sone kind of "constructor".
            @param acDbHelperObject PGSQLiteHelper object (in CertMaster it is the result of CertMasterDB() function).
            @return jQuery Deferred object            
            @link http://www.applicationcraft.com/developers/documentation/ac-mobile-build-phonegap/ac-mobile-build/ac-build-plugins/sqlite-plugin/
        */
        init: function(acDbHelperObject) {
            var dfd = $.Deferred();
            acDbHelperObject.openDatabase(function(db, version, systemPath) {
                _db = db;
                dfd.resolve({
                    db: db,
                    version: version,
                    systemPath: systemPath
                });
            }, function(er) {
                dfd.reject(er);
            });

            return dfd;
        },

        /*
            Check is database opened.
            @return Boolean
        */
        isOpen: function() {
            return (_db) ? true : false;
        },

        /*
            Perform any request to the database.
            If success, the array of rows will be passed as set of done() params.
            @param query Valid SQLite request string
            @return jQuery Deferred object
        */
        exec: function(query) {
            var dfd = $.Deferred();
            _db.executeSql(query, function(data) {
                dfd.resolve(data);
            }, function(er) {
                dfd.reject(er);
            });    

            return dfd;
        },

        /*
            Perform INSERT request to the database.            
            @param table String name of the table
            @param item Object. Key is a column name.
            @return jQuery Deferred object
        */
        insert: function(table, item) {
            var dfd = $.Deferred();
            _db.insert(table, item, function() {
                dfd.resolve();
            }, function(er) {
                dfd.reject(er);
            });

            return dfd;
        },

        /*
            Perform UPDATE request to the database.            
            @param table String name of the table
            @param item Object. Key is a column name. This param contain new field values.
            @param where String the optional WHERE clause to apply when updating. Passing null will update all rows.
            @param param Array You may include ?s in where, which will be replaced by the values from whereArgs, in order that they appear in the where. The values will be bound as Strings.
            @return jQuery Deferred object
        */    
        update: function(table, item, where, param) {
            var dfd = $.Deferred();
            _db.update(table, item, where, param, function() {
                dfd.resolve();
            }, function(er) {
                dfd.reject(er);
            });

            return dfd;
        },

        /*
            Perform DELETE request to the database.            
            @param table String name of the table
            @param where String the optional WHERE clause to apply when deleting. Passing null will delete all rows.
            @param param Array You may include ?s in where, which will be replaced by the values from whereArgs, in order that they appear in the where. The values will be bound as Strings.
            @return jQuery Deferred object
        */  
        delete: function(table, where, param) {
            var dfd = $.Deferred();
            _db.del(table, where, param, function(count) {
                dfd.resolve(count);
            }, function(er) {
                dfd.reject(er);
            });

            return dfd;
        },

        /*
            Perform SELECT request to the database.            
            @param table String name of the table
            @param columns Array A list of which columns to return.
            @param where String the optional WHERE clause to apply when querying. Passing null will query all rows.
            @param whereArgs Array You may include ?s in where, which will be replaced by the values from whereArgs, in order that they appear in the where. The values will be bound as Strings.
            @param groupBy String A filter declaring how to group rows, formatted as an SQL GROUP BY clause (excluding the GROUP BY it__self__). Passing null will cause the rows to not be grouped.
            @param having String A filter to declare which row groups to include in the cursor, if row grouping is being used, formatted as an SQL HAVING clause (excluding the HAVING it__self__).
            @param orderBy String How to order the rows, formatted as an SQL ORDER BY clause (excluding the ORDER BY it__self__). Passing null will use the default sort order, which may be unordered
            @param limit String Limits the number of rows returned by the query, formatted as LIMIT clause. Passing null denotes no LIMIT clause            
            @return jQuery Deferred object
        */  
        query: function(table, columns, where, whereArgs, groupBy, having, orderBy, limit) {
            var dfd = $.Deferred();

            _db.query(table, columns, where, whereArgs, groupBy, having, orderBy, limit, function(res) {
                dfd.resolve(res.rows);
            }, function(er) {
                dfd.reject(er);
            });

            return dfd;
        }
    };

    return SQLiteDatabaseAgent;    
})();

files.ApiRequester:
/*
This class implements server API methods call.
@link http://api.jquery.com/category/deferred-object/
*/
var ApiRequester = (function() {            
        var BASE_URL = 'http://ikandrakhina-server.nodejitsu.com/v1/', //CHANGE THIS IN CASE OF ANOTHER API URL
			METHODS = {
				POST: 'post',
				GET: 'get'
			};

		/*
			Private method to implement any type of server call.
			@param url String Relative URL without the BASE_URL part.
			@param method String. Get or Post. Please user the METHODS constant. 
			@params params Object
		*/	
		function _exec(url, method, params) {            
            return $.ajax({
                type: method,
                url: BASE_URL + url,
                data: params,
                crossDomain: true,
                xhrFields: {
                    withCredentials: true
                }
            });            
		}

        function _execWithFileTransfer(url, fileUri, params) {
            var dfd = $.Deferred();

            var options = new FileUploadOptions();
            options.fileKey = "image";
            options.fileName = fileUri.substr(fileUri.lastIndexOf('/') + 1);
            options.mimeType = "image/jpeg";
            options.chunkedMode = false;

            options.params = params;

            var ft = new FileTransfer();
            ft.upload(fileUri, BASE_URL + url, function() {
                dfd.resolve();
            }, function(err) {
                showMessage('File Transfer Err', JSON.stringify(err))
                dfd.reject(err);
            }, options);

            return dfd;
        }

        var ApiRequester = {                       	
	        	/*
					Login method.
					@param email String 
					@param password String
	        	*/
	        	login: function(email, password) {
	        		var options = {
	        			email: email,
	        			password: password
	        		};

	        		return _exec('login', METHODS.POST, options);
	        	},

	        	/*
	        		Logout method
	        	*/
	        	logout: function() {
	        		return _exec('logout', METHODS.POST, undefined);
	        	},

				/*
					Create a new user
					@param email String 
					@param password String
                    @param user Object(name TEXT, state TEXT, country TEXT, zipcode TEXT, phone TEXT)
				*/
	        	createUser: function(user) {
                    console.log("create user", user);

	        		return _exec('users', METHODS.POST, user);	
	        	},

                /*
                    Update user data
                    @param userId String Id of the user.
                    @param options Object
                */
                updateUser: function(userId, user) {
                    return _exec('users/' + userId, METHODS.POST, user);
                },

                /*
                	Upload a cert to the server.
					@param userId String Id of the user.
					@param certObj Object, Fields: id_cert (String), last_modified (Time in Miliseconds), name (String), cert_number (String)
                */
                uploadCert: function(userId, certObj) {
                    var image = certObj.image;
                    delete certObj.image;

                    var options = {
                        cert: JSON.stringify(certObj)
                    }


                    var reqUrl = 'users/' + userId + '/certs';

                    if (_isSQLiteNull(image)) {
                        return _exec(reqUrl, METHODS.POST, options);
                    } else {
                        return _execWithFileTransfer(reqUrl, image, options);
                    }
	        	},

                /*
		            In the case of success will pass an Array of user certs as a param in done() callback.
		            @param userId String Id of the user.
		            @param lastSyncDate Number Optional. Time in Miliseconds.
		            @return jQuery Deferred object
		        */
                getUserCerts: function(userId, lastSyncDate) {
                    var options = (lastSyncDate) ? {last_sync: lastSyncDate} : undefined;
                    return _exec('users/' + userId + '/certs', METHODS.GET, options);    
            	}	        	
	        };

        return ApiRequester;
})();

files.LocalDBRequester:
/*
This class iplements request to the local database.
This requests include some business logic required for CertMaster.
@link http://api.jquery.com/category/deferred-object/
*/

var LocalDBRequester = (function() {                   
    var TABLE = {
        CERTS: 'certs',
        USERS: 'users'
    };

    var LocalDBRequester = {        
        /*
            This method have to be called first to init the class.
            This is sone kind of "constructor".
            @return jQuery Deferred object
        */
        init: function() {
            return SQLiteDatabaseAgent.init(new CertMasterDB());
        },

        /*
            In case of sucess will pass the Array of user certs as a param of done() callback.
            @param userId String Id of the user.
            @param lastSyncDate Number Optional. Time in Miliseconds.
            @param hideRemoved Boolean If true - will hide deleted certs from the reuslt.
            @return jQuery Deferred object
        */
        getUserCerts: function(userId, lastSyncDate, hideRemoved) {
            var cond = ["(id_user = '" + userId + "')"];
            if (lastSyncDate) {
                cond.push("(last_modified > " + lastSyncDate + ")");
            }

            if (hideRemoved) {
                cond.push("(removed IS NOT 1)");
            }

            var dfd = $.Deferred();            
            SQLiteDatabaseAgent.exec("SELECT * FROM certs WHERE " + cond.join(" AND ") + ' ORDER BY name COLLATE NOCASE ASC').done(function(data) {
                dfd.resolve(data.rows)
            }).fail(function(er) {
                dfd.reject(er);
            });

            return dfd;
        },

        applyUseridToLocalCerts: function(userId) {
            if(!userId) {
                showMessage("Error", "user is undefined");
                return;
            }
            return SQLiteDatabaseAgent.update(TABLE.CERTS, {id_user: userId}, 'id_user = ?', [DEFAULT_USER._id]);
        },

        /*
            Add new cert to user.
            @param cert Object. Contain fields: String id_cert, String id_user, String name, String cert_number, 
            Time in miliseconds last_modified, removed (1 or 0).     
            @return jQuery Deferred object                   
        */
        addCert: function(cert) {
            return SQLiteDatabaseAgent.insert(TABLE.CERTS, cert);
        },

        /*
            Update cert in database.
            @param cert Object. Contain fields: String id_cert, String id_user, String name, String cert_number, 
            Time in miliseconds last_modified, removed (1 or 0).     
            @return jQuery Deferred object                   
        */
        updateCert: function(cert) {
            return SQLiteDatabaseAgent.update(TABLE.CERTS, cert, 'id_cert = ?', [cert.id_cert]);
        },

        getCert: function(certId) {
            var dfd = $.Deferred();
            SQLiteDatabaseAgent.exec("SELECT * FROM certs WHERE id_cert = '" + certId + "'").done(function(data) {                
                dfd.resolve(data.rows[0]);
            }).fail(function() {
                dfd.reject();
            });
            return dfd;
        },

        /*
            Update cert in database if cert exists, Add otherwise.
            @param cert Object. Contain fields: String id_cert, String id_user, String name, String cert_number, 
            Time in miliseconds last_modified, removed (1 or 0).     
            @return jQuery Deferred object                   
        */
        addOrUpdateCert: function(cert) {
            var dfd = $.Deferred();
            var success = function() {
                dfd.resolve();
            };
            var fail = function() {
                dfd.reject();
            };

            LocalDBRequester.getCert(cert.id_cert).done(function(existingCert) {
                if (existingCert) {
                    LocalDBRequester.updateCert(cert).done(success).fail(fail);
                } else {
                    LocalDBRequester.addCert(cert).done(success).fail(fail);
                }
            }).fail(fail);

            return dfd;
        },

        /*
            Mark user cert as removed.
            @param certId String
            @return jQuery Deferred object
        */
        removeCert: function(certId) {
            var lastModified = _getUTCTimestamp(new Date());
            return SQLiteDatabaseAgent.update(TABLE.CERTS, {last_modified: lastModified, removed: 1}, 'id_cert = ?', [certId]);
        },

        /**
            This method used fo test in most cases.
            Please DO NOT use it for production.
            Remove all user certs form local DB.
            @param userId String
            @return jQuery Deferred object
        */
        clearCerts: function(userId) {            
            return SQLiteDatabaseAgent.delete(TABLE.CERTS, 'id_user = ?', [userId]);            
        },

        /*
            In case of sucess will pass time in miliseconds as a param of done() callback. 
            Result also can contain NULL value.
            @param userId String Id of the user. It have to be valid as NodeJS _id of the object.
            @return jQuery Deferred object
        */
        getLastSyncDate: function(userId) {
            var dfd = $.Deferred();
            SQLiteDatabaseAgent.query(TABLE.USERS, ['last_sync'], 'id_user = ?', [userId]).done(function(rows) {                
                dfd.resolve(rows[0].last_sync);
            }).fail(function() {
                dfd.reject();
            });

            return dfd;
        },

        /*
            Set new last sync date for user.
            Result also can contain NULL value.
            @param userId String Id of the user. It have to be valid as NodeJS _id of the object.
            @param userId Number Time in Miliseconds.
            @return jQuery Deferred object
        */
        updateLastSyncDate: function(userId, newDate) {
            return LocalDBRequester.updateUser({
                id_user: userId, 
                last_sync: newDate
            });
        },

        /*
            Update user data in the DB.
            @param user Object. Fields: id_user (String), email (String), last_sync (Time in miliseconds)
            @return jQuery Deferred object
        */
        updateUser: function(user) {
            return SQLiteDatabaseAgent.update(TABLE.USERS, user, 'id_user = ?', [user.id_user]);
        },

        /*
            Get row form the user table. The result can be Array or NULL.
            Result passed to the done() callback.
            @param userId String Id of the user.
            @return jQuery Deferred object
        */
        getUser: function(userId) {
            var dfd = $.Deferred();
            SQLiteDatabaseAgent.exec("SELECT * FROM users WHERE id_user = '" + userId + "'").done(function(data) {                
                dfd.resolve(data.rows[0]);
            }).fail(function(err) {
                dfd.reject(err);
            });

            return dfd;
        },

        /*
            Add user data into the DB.
            @param user Object. Fields: id_user (String), email (String), last_sync (Time in miliseconds)
            @return jQuery Deferred object
        */
        addUser: function(user) {
            return SQLiteDatabaseAgent.insert(TABLE.USERS, user);
        },

        /*
            Check is user exists. If exists - update, otherwise add.
            @param user Object. Fields: id_user (String), email (String), last_sync (Time in miliseconds)
            @return jQuery Deferred object
        */
        addOrUpdateUser: function(user) {
            var dfd = $.Deferred();
            var success = function() {
                dfd.resolve();
            };
            var fail = function(err) {
                dfd.reject(err);
            };

            LocalDBRequester.getUser(user.id_user).done(function(existingUser) {
                if (existingUser) {
                    LocalDBRequester.updateUser(user).done(success).fail(fail);
                } else {
                    LocalDBRequester.addUser(user).done(success).fail(fail);
                }
            }).fail(fail);

            return dfd;
        }
    };

    return LocalDBRequester;
})();

files.DBStructure:
/**
 * This function creates or updates the structure of the SQLite database.
 * CREATE_BATCH - is the set of SQL queries that performed when db created (on first run typically)
 * UPDATE_BATCH - will be used in case of update the db structure.
 * UPDATE based on DATABASE_VERSION
 */
function CertMasterDB() {    
    PGSQLiteHelper.call(this);

    this.DATABASE_VERSION = 5;
    this.DATABASE_NAME = "certmaster";

    var CREATE_TABLE = {
        CERTS: "CREATE TABLE certs (id_cert TEXT PRIMARY KEY, id_user TEXT, name TEXT, cert_number TEXT, last_modified TEXT, local_medium TEXT, local_thumb TEXT, remote_medium TEXT, remote_thumb TEXT, expired_date TEXT, removed INTEGER)",
        USERS: "CREATE TABLE users (id_user TEXT PRIMARY KEY UNIQUE, email TEXT UNIQUE, last_sync TEXT, name TEXT, state TEXT, country TEXT, zipcode TEXT, phone TEXT, primary_license TEXT, secondary_license TEXT)"
    };

    this.UPDATE_BATCH = [
            [], 
            [   "DROP TABLE IF EXISTS certs", "DROP TABLE IF EXISTS users", CREATE_TABLE.CERTS, CREATE_TABLE.USERS],

            [   "ALTER TABLE users ADD name TEXT", 
                "ALTER TABLE users ADD state TEXT", 
                "ALTER TABLE users ADD country TEXT", 
                "ALTER TABLE users ADD zipcode TEXT", 
                "ALTER TABLE users ADD phone TEXT"],

            [   "ALTER TABLE users ADD primary_license TEXT",
                "ALTER TABLE users ADD secondary_license TEXT"]    
        ];    

    this.CREATE_BATCH = [CREATE_TABLE.CERTS, CREATE_TABLE.USERS];
}

files.fileProcesser:
var LOCAL_DIR = 'CertMasterRes';

var CertMasterFileProcesser = (function() {                   
    _localFileEntery = null;

    function _initFileEntry() {
        var dfd = $.Deferred();

        window.requestFileSystem(LocalFileSystem.PERSISTENT, 0, function(fileSystem){
            _localFileEntery = fileSystem.root.fullPath;
            dfd.resolve();
        }, function(err){
            //self.warnings({code : 3112, message : "Error requestFileSystem"});
            dfd.reject(err);
        });

        return dfd;
    }

    function _download(url, overwrite){               
        var dfd = $.Deferred(),            
            getName = function(path){
                var res = path;
                if (typeof path == "string"){
                    var pos = path.lastIndexOf("/");
                    if (pos != -1) {
                        res = path.substring(pos + 1, path.length);
                    }
                }

                return res;
            },
            downloadByURI = function(uri, filePath) {
                var dfd = $.Deferred(),
                    fileTransfer = new FileTransfer();

                fileTransfer.download(uri, filePath, function(entry) {
                        dfd.resolve(entry.fullPath);
                    }, function(err) {
                        //self.warnings({code : 3113, message : "Error download resource " + url + "::" + JSON.stringify(err)});
                        dfd.reject(err);
                    }
                );

                return dfd;
            },
            fail = function(err) {
                dfd.reject(err);
            };

        var uri = encodeURI(url),
            filePath = this._localFileEntery + "/" + LOCAL_DIR + "/" + getName(url);

        if (overwrite) {
            _remove(filePath).done(function() {
                downloadByURI(uri, filePath).done(function(uri) {
                    dfd.resolve(uri);
                }).fail(fail);
            }).fail(fail);
        } else {
            dfd = downloadByURI(uri, filePath);
        }            

        return dfd;            
    }

    function _copy(fromUri) {
        var dfd = $.Deferred();
            direcoryPath = this._localFileEntery + "/" + LOCAL_DIR,
            fail = function(err) {
                dfd.reject(err);
            };

        window.resolveLocalFileSystemURI(direcoryPath, function(dirEntry) {
            window.resolveLocalFileSystemURI(fromUri, function(fileEntry) {
                fileEntry.copyTo(dirEntry, undefined, function(newEntry) {
                    dfd.resolve(newEntry.fullPath);
                }, fail);            
            }, fail);            
        }, fail);

        return dfd;
    }

    function _remove(fileUri) {
        var dfd = $.Deferred(),
            fail = function(err) {
                if (err.code == FileError.NOT_FOUND_ERR) {
                    dfd.resolve();
                } else {
                    dfd.reject(err);
                }
            };

        window.resolveLocalFileSystemURI(fileUri, function(fileEntry) {
            fileEntry.remove(function() {
                dfd.resolve();
            }, fail);
        }, fail);

        return dfd;
    }

    var CertMasterFileProcesser = {                    
        download: function(url) {
            var dfd = $.Deferred(),
                fail = function(err) {
                    dfd.reject(err);
                };

            if (!_localFileEntery) {
                _initFileEntry().done(function() {
                    _download(url).done(function(uri) {
                        dfd.resolve(uri);
                    }).fail(fail);
                }).fail(fail);
            } else {
                dfd = _download(url);
            }   

            return dfd;
        },

        copy: function(fileUri) {
            var dfd = $.Deferred(),
                fail = function(err) {
                    dfd.reject(err);
                };

            if (!_localFileEntery) {
                _initFileEntry().done(function() {
                    _copy(fileUri).done(function(uri) {
                        dfd.resolve(uri);
                    }).fail(fail);
                }).fail(fail);
            } else {
                dfd = _copy(fileUri);
            }   

            return dfd;  
        }
    };

    return CertMasterFileProcesser;
})();

