files.RegistrationPage:
var W_USER_EMAIL = 'userEmail',
    W_USER_PASSWORD = 'userPassword',
    W_USER_CONFIRM_PASSWORD = 'userConfirmPassword',
    W_USER_NAME = 'userName',
    W_USER_STATE = 'userState',
    W_USER_COUNTRY = 'userCountry',
    W_USER_ZIPCODE = 'userZipcode',
    W_USER_PHONE = 'userPhone',

    W_USER_PRIMARY_LICENSE = 'primaryLicense',
    W_USER_PRIMARY_LIST = 'primaryList',

    W_USER_SECONDARY_LICENSE = 'secondaryLicense',
    W_USER_SECONDARY_LIST = 'secondaryList',

    W_REGISTRATE_BTN = 'registrate_btn';

function handler_registrate_btn_onClick(mouseev){

    var user = {};

    user.email = trim(app.w(W_USER_EMAIL).value());
    user.password = trim(app.w(W_USER_PASSWORD).value());
    user.name = trim(app.w(W_USER_NAME).value());
    user.state = trim(app.w(W_USER_STATE).value());
    user.country = trim(app.w(W_USER_COUNTRY).value());
    user.zipcode = trim(app.w(W_USER_ZIPCODE).value());
    user.phone = trim(app.w(W_USER_PHONE).value());
    user.primary_license = g_primaryLicenseController.getValue();
    user.secondary_license = g_secondaryLicenseController.getValue();

    if (!isValidEmail(user.email)) {
        showMessage("Error", "Email is not valid");
        return;
    }

    if(user.password == '' || user.password != trim(app.w(W_USER_CONFIRM_PASSWORD).value()) ) {
        showMessage("Error", '"Password" and "Confirm Password" are entered incorrectly');
        return;
    }

    if (!validRequiredUserFields(user)) {
        showMessage("Error", 'All fields are required');
        return;
    }

    twirlyThing(true);
    // request authentication
    ApiRequester.createUser(user).done(function(newUser) {        
        updateUserData(newUser);
    }).fail(function( jqXHR, textStatus ) {
        showMessage('Registration FAILED', textStatus);
    }).always(twirlyThing(false));

}

