            throw "obj must not be null";
        }

        if (this.isBound(eventType, obj, methodName)) {
            return;
        }

        var eventHandlers = this._eventsHandlers[eventType];
        if (eventHandlers == null) {
            eventHandlers = [];
            this._eventsHandlers[eventType] = eventHandlers;
        }

        eventHandlers.push({
            obj: obj,
            methodName: methodName
