/**
 * @event onClick - Called when the mouse button is clicked.
 * @event onDoubleClick - When the mouse button is double clicked
 * @event onMouseDown - Called as soon as the mouse button is pressed.
 * @event onMouseUp - Called as soon as the mouse button is released.
 * @event onMouseEnter - When the mouse moves over the Widget this is called.
 * @event onMouseLeave - Called is the mouse is over a Widget and the user then moves the mouse away from the Widget.
 * @event onDragStart
 * @event onDragStop
 * @event onResizeStart
 * @event onResizeStop
 */
function WidgetController(widget) {
    if (widget == null) {
        throw "widget must not be null";
    }

    EventBinder.call(this);
    var selfWC = this;

    this._widget = widget;
    this._jwidget = $(widget);
    this._widgetEventHandlers = {};

    this._lastEvent = false;

    this.getWidget = function() {
        return this._widget;
    };

    this.getWidgetName = function() {
        return this._widget.name();
    };

    this.visible = function(value) {
        this._widget.visible(value);    

        return this._widget.visible();
    };

    this.value = function(value) {
        if ( value != null) {
            this.getWidget().value(value);
        }    
        var newValue = this.getWidget().value();
        return (typeof(newValue) == "object" ) ? newValue.value : (newValue) ? newValue : '';
    };

    /**
     * Executes when first event handler was binded to the event
     * @param {string} eventType
     * @override
     */
    this._onEventBound = function(eventType) {
        if (!selfWC._lastEvent || selfWC._lastEvent != eventType) {
            selfWC._lastEvent = eventType;
            var timeoutID = setTimeout(function(){
                selfWC._lastEvent = false;
                clearTimeout(timeoutID);
            }, 10);

            var widgetEventId = this._getWidgetEventId(eventType);
            if (widgetEventId != null) {
                var self = this;
                var handler = function(widgetEvent) {
                    var argumentsArray = Array.prototype.slice.call(arguments);
                    var args = [eventType, self].concat(argumentsArray);

                    self.trigger.apply(self, args);
                };

                this._widgetEventHandlers[eventType] = handler;

                this._jwidget.unbind(widgetEventId);
                this._jwidget.bind(widgetEventId, handler);
                this._widget.bindEvent(widgetEventId);
            }


        }
        else {
            console.log("left event");
        }

    };

    /**
     * Executes when last event handler was unbinded from the event
     * @param {string} event
     * @override
     */
    this._onEventNotBound = function(eventType) {
        var widgetEventId = this._getWidgetEventId(eventType);
        if (widgetEventId != null) {
            var handler = this._widgetEventHandlers[eventType];
            delete this._widgetEventHandlers[eventType];
            this._jwidget.unbind(widgetEventId, handler);
            this._widget.unbindEvent(widgetEventId, true);
        }
    };

    /**
     * Maps eventType to the widget event id
     * @param {string} eventType - Type of the event
     * @return {string} widget event id if mapping exists, null otherwise
     */
    this._getWidgetEventId = function(eventType) {
        switch (eventType) {
            case "onClick":
                return AC.Widgets.Base.onClick;
            case "onDoubleClick":
                return AC.Widgets.Base.onDbClick;
            case "onMouseDown":
                return AC.Widgets.Base.onMouseDown;
            case "onMouseUp":
                return AC.Widgets.Base.onMouseUp;
            case "onMouseEnter":
                return AC.Widgets.Base.onMouseEnter;
            case "onMouseLeave":
                return AC.Widgets.Base.onMouseLeave;
            case "onDragStart":
                return AC.Widgets.Base.onDragStart;
            case "onDragStop":
                return AC.Widgets.Base.onDragStop;
            case "onResizeStart":
                return AC.Widgets.Base.onResizeStart;
            case "onResizeStop":
                return AC.Widgets.Base.onResizeStop;

            default:
                return null;
        }
    };
}

