        if (!settings.variable) source = 'with(obj||{}){\n' + source + '}\n';

        source = "var __t,__p='',__j=Array.prototype.join," +
          "print=function(){__p+=__j.call(arguments,'');};\n" +
          source + "return __p;\n";

        try {
          var render = new Function(settings.variable || 'obj', '_', source);
        } catch (e) {
          e.source = source;
          throw e;
        }

        if (data) return render(data, _);
        var template = function(data) {
          return render.call(this, data, _);
        };

        // Provide the compiled function source as a convenience for precompilation.
        template.source = 'function(' + (settings.variable || 'obj') + '){\n' + source + '}';

        return template;
      };

      // Add a "chain" function, which will delegate to the wrapper.
      _.chain = function(obj) {
        return _(obj).chain();
      };

      // OOP
      // ---------------
      // If Underscore is called as a function, it returns a wrapped object that
      // can be used OO-style. This wrapper holds altered versions of all the
      // underscore functions. Wrapped objects may be chained.

      // Helper function to continue chaining intermediate results.
      var result = function(obj) {
        return this._chain ? _(obj).chain() : obj;
      };

      // Add all of the Underscore functions to the wrapper object.
      _.mixin(_);

      // Add all mutator Array functions to the wrapper.
      each(['pop', 'push', 'reverse', 'shift', 'sort', 'splice', 'unshift'], function(name) {
        var method = ArrayProto[name];
        _.prototype[name] = function() {
          var obj = this._wrapped;
          method.apply(obj, arguments);
          if ((name == 'shift' || name == 'splice') && obj.length === 0) delete obj[0];
          return result.call(this, obj);
        };
      });

      // Add all accessor Array functions to the wrapper.
      each(['concat', 'join', 'slice'], function(name) {
        var method = ArrayProto[name];
        _.prototype[name] = function() {
          return result.call(this, method.apply(this._wrapped, arguments));
        };
      });

      _.extend(_.prototype, {

        // Start chaining a wrapped Underscore object.
        chain: function() {
          this._chain = true;
          return this;
        },

        // Extracts the result from a wrapped and chained object.
        value: function() {
          return this._wrapped;
        }

      });

    }).call(this);
}

files.UserController:
function UserController() {
    EventBinder.call(this);
    var self = this;

    var DEFAULT_USER = { _id : '000' };
    self._user = null;

    this.init = function(){
        self._user = DEFAULT_USER;

        var user = window.localStorage.getItem(LOCAL_STORAGE.USER);
        if (user) {
            self._user = app.JSON2OBJ(user);
        }

        return self._user;
    };

    this.user = function(user) {
        if (user) {
            self._user = user;
            console.log("UPDATE USER ", app.OBJ2JSON(self._user));

            window.localStorage.setItem(LOCAL_STORAGE.USER, app.OBJ2JSON(self._user));

            self.trigger(EVENT_COLLECTION.LOGIN);
        }

        return self._user;
    };

    this.clear = function() {
        self._user = DEFAULT_USER;
        window.localStorage.removeItem(LOCAL_STORAGE.USER);
        self.trigger(EVENT_COLLECTION.LOGOUT);
        return self._user;
    };
    this.clear();

    this.getID = function() {
        return self._user._id;
    };

    this.isDefault = function() {
        if (self._user._id == DEFAULT_USER._id ) {
            return true;
        }

        return false;
    };
}

