/*  MAIN MENU   */
var W_MENU_USER_ACCOUNT_BTN = 'menu_account_btn';
var W_MENU_LOGOUT_BTN = 'menu_logout_btn';
var W_MENU_SIGNIN_BTN = 'menu_signin_btn';

function BtnControllerForAuthorized(widgetBtn, unauth) {
    assert(widgetBtn != null);
    WidgetController.call(this, widgetBtn);
    var self = this;

    this._unauth = unauth || false;

    g_curUser.bind(EVENT_COLLECTION.LOGIN , this, '_changeState');
    g_curUser.bind(EVENT_COLLECTION.LOGOUT, this, '_changeState');

    this._changeState = function() {
        var state = self._unauth ? g_curUser.isDefault() : !g_curUser.isDefault();
        self.visible(state);
    }

    var state = self._unauth ? g_curUser.isDefault() : !g_curUser.isDefault();
    this.visible(state);

}

function initiateMainMenu () {
    var userAccountBtn = new BtnControllerForAuthorized(app.w(W_MENU_USER_ACCOUNT_BTN)); 
    var userLogoutBtn =  new BtnControllerForAuthorized(app.w(W_MENU_LOGOUT_BTN)); 
    var userLoginBtn =   new BtnControllerForAuthorized(app.w(W_MENU_SIGNIN_BTN), true); 

    userLogoutBtn.bind('onClick', g_appController, 'logout');
}

