function AppController() {
    EventBinder.call(this);
    var self = this;

    this.logout = function () {
        console.log("on logout");

        twirlyThing(true);
        ApiRequester.logout().done(function(data) {
            g_curUser.clear();
        }).fail(function() {
            showMessage('Logout FAIL');   
        }).always(twirlyThing(false));
    };

    this.login = function() {
        var login = app.w('txt_login1').value(),
        pass = app.w('txt_password1').value();

        twirlyThing(true);
        // request authentication
        ApiRequester.login(login, pass).done(function(user) {
            updateUserData(user);
        }).fail(function( jqXHR, textStatus ) {
            showMessage('Login FAILED on the Client', textStatus);
        }).always(twirlyThing(false));
    };
}

