/**
 * @event onChange
 * @event onKeyUp
 * @event onKeyDown
 */ 
function TextMobileWidgetController(widget) {
    WidgetController.call(this, widget);

    /**
    * Maps eventType to the widget event id
    * @param {string} eventType - Type of the event
    * @return {string} widget event id if mapping exists, null otherwise
    * @override
    */
    var superGetWidgetEventId = this._getWidgetEventId;
    this._getWidgetEventId = function(eventType) {
        switch (eventType) {
            case "onChange":
                return AC.Widgets.WiziCore_UI_TextMobileWidget.onChange;

            case "onKeyUp" :
                return AC.Widgets.WiziCore_UI_TextMobileWidget.onKeyUp;

            case "onKeyDown" :
                return AC.Widgets.WiziCore_UI_TextMobileWidget.onKeyDown;

            default:
                return superGetWidgetEventId.call(this, eventType);
        }
    };
}

