/**  PicklistController  
 * @param wText widget textMobile
 * @param wList widget listMobile
 * @param values Array of Objects {'values' : string (lowercase), 'label' : string }  */
function PicklistController(wText, wList, values) {
    assert(wText != null);
    assert(wList != null);

    EventBinder.call(this);
    var self = this;

    this._searchField = new TextMobileWidgetController(wText);
    this._searchField.bind("onKeyUp", this, '_onSearch');

    this._searchList = new ListMobileWidgetController(wList);
    this._searchList.populate([]);
    this._searchList.bind('onClick', this, '_selectValue');

    this._values = values;
    this._selectedValue = null;

    this.getValue = function() {
        return this._selectedValue ? this._selectedValue.label : this._searchField.value();
    };

    this.setValue = function(value) {
        if(!value) {
            this._searchField.value('');
            return;
        }

        var exist = _.where(this._values, {value : value.toLowerCase()});

        if (exist && exist.length > 0) {
            this._selectedValue = exist;
            this._searchField.value(exist[0].label);
            return ;
        }

        this._searchField.value(value);
    };


    this._requestid = 0;
    this._onSearch = function() {
        self._requestid++;
        var i = self._requestid;

        g_timeoutID = setTimeout(function(){

            if (i != self._requestid) {
                return;
            }
            self._selectedValue = null;

            var search = trim(self._searchField.getWidget().value());
            if (search.length > 0) {
                /* SEARCH */
                self._getSearchResults(search);
            }
            else {
                self._searchList.populate([]);
            }

            self._requestid = 0;
            g_timeoutID = null;

        }, SEARCH_TIMEOUT);
    };

    this._getSearchResults = function(searchText) {
        searchText = searchText.toLowerCase();

        var enabledValues = _.filter(self._values, function(value) {
            var str = value.value.toLowerCase();
            if (str.search(searchText) != -1) return true;
        });

        self._searchList.populate(enabledValues);

    };

    this._selectValue = function(obj, event, newValue, pos) {
        self._selectedValue = newValue;
        self._searchField.value(self._selectedValue.label);
        self._searchList.populate([]);
    };



}

