function ListMobileWidgetController(widget, map) {
    WidgetController.call(this, widget);

    this._map = map || {
        'label': 'label',
        'value': 'value',
        'count': 'count',
        'aside': 'aside',
        'image': 'img',
        'divider' : 'separator'        
    };

    this.populate = function(values) {
        var wName = this.getWidgetName();

        app.populateWidget(wName, values, this._map);
    };

    /**
    * Maps eventType to the widget event id
    * @param {string} eventType - Type of the event
    * @return {string} widget event id if mapping exists, null otherwise
    * @override
    */
    var superGetWidgetEventId = this._getWidgetEventId;
    this._getWidgetEventId = function(eventType) {
        switch (eventType) {
            case "onClick":
                return AC.Widgets.WiziCore_UI_ListMobileWidget.onClick;

            default:
                return superGetWidgetEventId.call(this, eventType);
        }
    };

}
